mapper.select:
select a.id, a.kindergarten_id, a.name, a.sex, a.tel, a.type, a.age, a.recharge_tel_no, a.photo, a.create_user,a.create_time, i.name kindergartenName, u.name createUserName,(select group_concat(distinct g.id) from t_kindergarten_grade g where g.first_teacher = a.id or g.second_teacher = a.id or g.nurse = a.id) gradeNum from t_kindergarten_teacher a left join t_kindergarten_info i on a.kindergarten_id = i.id left join t_sys_user u on a.create_user = u.id where 1=1
and a.name like CONCAT('%', #{name, jdbcType=VARCHAR}, '%')
and a.type = #{type, jdbcType=INTEGER}
and a.kindergarten_id = #{kindergartenId, jdbcType=INTEGER}
order by a.create_time limit #{offset}, #{pageSize}

select count(1) from t_kindergarten_teacher a left join t_kindergarten_info i on a.kindergarten_id = i.id where 1=1
and a.name like CONCAT('%', #{name, jdbcType=VARCHAR}, '%')
and a.type = #{type, jdbcType=INTEGER}
and a.kindergarten_id = #{kindergartenId, jdbcType=INTEGER}
